import React, { useState } from "react";
import "./Contact.css";

export default function Contact() {
  const [status, setStatus] = useState("idle");
  const [cpf, setCpf] = useState("");
  const [cpfError, setCpfError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("loading");
    setCpfError("");

    // Validação do CPF via API 4devs-br
    try {
      const response = await fetch(`https://www.4devs.com.br/ferramentas_online.php?acao=validar_cpf&cpf=${cpf}`);
      const text = await response.text();

      // A API retorna "true" ou "false" como texto
      if (text.trim() !== "true") {
        setCpfError("CPF inválido!");
        setStatus("idle");
        return;
      }

      // Simula envio assíncrono do formulário
      setTimeout(() => {
        setStatus("success");
        setTimeout(() => setStatus("idle"), 2500);
      }, 1200);

    } catch (error) {
      console.error(error);
      setCpfError("Erro ao validar CPF. Tente novamente.");
      setStatus("idle");
    }
  };

  return (
    <section id="contact" className="contact-section">
      <h2>Entre em Contato</h2>

      <form className="contact-form" onSubmit={handleSubmit} noValidate>
        <input name="name" type="text" placeholder="Seu nome" required />
        <input name="email" type="email" placeholder="Seu e-mail" required />

        <input
          name="cpf"
          type="text"
          placeholder="Seu CPF"
          value={cpf}
          onChange={(e) => setCpf(e.target.value)}
          required
        />
        {cpfError && <p className="error">{cpfError}</p>}

        <textarea name="message" placeholder="Sua mensagem" required />

        <button
          type="submit"
          className={`submit-btn ${status === "success" ? "success" : ""}`}
          disabled={status === "loading"}
        >
          {status === "loading" ? "Enviando..." : status === "success" ? "Mensagem enviada! ✓" : "Enviar"}
        </button>
      </form>
    </section>
  );
}
