const handleSubmit = async (e) => {
  e.preventDefault();
  setStatus("loading");
  setCpfError("");

  // Remove formatação do CPF
  const cpfSemFormatacao = cpf.replace(/[^\d]+/g, "");

  // Validação do CPF via API 4Devs
  try {
    const response = await fetch(`https://www.4devs.com.br/ferramentas_online.php?acao=validar_cpf&cpf=${cpfSemFormatacao}`);
    const text = await response.text();

    if (text.trim() !== "true") {
      setCpfError("CPF inválido!");
      setStatus("idle");
      return;
    }

    // Simula envio assíncrono do formulário
    setTimeout(() => {
      setStatus("success");
      setTimeout(() => setStatus("idle"), 2500);
    }, 1200);

  } catch (error) {
    console.error(error);
    setCpfError("Erro ao validar CPF. Tente novamente.");
    setStatus("idle");
  }
};
import React, { useState } from "react";
