import React, { useState, useEffect } from "react";

export default function Header() {
  const [open, setOpen] = useState(false);

  useEffect(() => {
    const handleKey = (e) => {
      if (e.key === "Escape" && open) setOpen(false);
    };
    document.addEventListener("keydown", handleKey);
    return () => document.removeEventListener("keydown", handleKey);
  }, [open]);

  useEffect(() => {
    document.body.style.overflow = open ? "hidden" : "auto";
  }, [open]);

  const links = [
    {href:"#home", label:"Inicio"},
    {href:"#about", label:"Sobre"},
    {href:"#gallery", label:"Galeria"},
    {href:"#contact", label:"Contato"}
  ];

  return (
    <header>
      <nav id="navbar" className="nav-container">
        <div className="logo">
          <img src="/images/RiosProFuturoLOGO.png" alt="logo" width="48" />
          <span>Rios Pro Futuro</span>
        </div>
        <button
          id="mobileMenuToggle"
          aria-expanded={open}
          aria-controls="mobileMenu"
          onClick={() => setOpen(!open)}
          className={open ? "active mobile-toggle" : "mobile-toggle"}
        >
          <span className="sr-only">Abrir menu</span>
          â˜°
        </button>

        <ul className="nav-links">
          {links.map((l) => (
            <li key={l.href}><a href={l.href}>{l.label}</a></li>
          ))}
        </ul>

        <div id="mobileMenu" className={open ? "mobile-menu active" : "mobile-menu"}>
          <ul className="mobile-nav-links">
            {links.map((l) => (
              <li key={l.href}><a href={l.href} onClick={() => setOpen(false)}>{l.label}</a></li>
            ))}
          </ul>
        </div>
      </nav>
    </header>
  );
}
