import React, { useState } from "react";
import axios from "axios";
import "./Contact.css";

export default function Contact() {
  const [status, setStatus] = useState("idle");
  const [cep, setCep] = useState("");
  const [cepError, setCepError] = useState("");
  const [cepData, setCepData] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("loading");
    setCepError("");
    setCepData(null);

    const cepLimpo = cep.replace(/[^\d]+/g, "");

    try {
      const response = await axios.get(
        `http://localhost:5000/consulta-cep/${cepLimpo}`
      );

      if (!response.data.valid) {
        setCepError("CEP inválido!");
        setStatus("idle");
        return;
      }

      setCepData(response.data.data);
      setStatus("success");
    } catch (error) {
      console.error(error);
      setCepError("Erro ao consultar CEP. Tente novamente.");
      setStatus("idle");
    }
  };

  return (
    <section id="contact" className="contact-section">
      <h2>Consulta de CEP</h2>

      <form className="contact-form" onSubmit={handleSubmit} noValidate>
        <input
          name="cep"
          type="text"
          placeholder="Digite o CEP"
          value={cep}
          onChange={(e) => setCep(e.target.value)}
          required
        />
        {cepError && <p className="error">{cepError}</p>}

        <button
          type="submit"
          className={`submit-btn ${status === "success" ? "success" : ""}`}
          disabled={status === "loading"}
        >
          {status === "loading"
            ? "Consultando..."
            : status === "success"
            ? "CEP encontrado! ✓"
            : "Consultar"}
        </button>
      </form>

      {cepData && (
        <div className="cep-result">
          <p><strong>Logradouro:</strong> {cepData.logradouro}</p>
          <p><strong>Bairro:</strong> {cepData.bairro}</p>
          <p><strong>Cidade:</strong> {cepData.localidade}</p>
          <p><strong>Estado:</strong> {cepData.uf}</p>
        </div>
      )}
    </section>
  );
}
