// src/components/CPFValidation.jsx
import React, { useState } from "react";
import axios from "axios";
import "./Contact.css"; // mantém seu estilo atual

export default function CPFValidation() {
  const [cpf, setCpf] = useState("");
  const [status, setStatus] = useState("idle"); // idle | loading | success | error
  const [valid, setValid] = useState(null);
  const [error, setError] = useState("");

  const sanitize = (value) => value.replace(/\D/g, "");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("loading");
    setError("");
    setValid(null);

    const onlyDigits = sanitize(cpf);

    if (onlyDigits.length !== 11) {
      setStatus("error");
      setError("Informe um CPF com 11 dígitos.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/validate-cpf", { cpf: onlyDigits });
      if (response.data.valid === true) {
        setValid(true);
        setStatus("success");
      } else {
        setValid(false);
        setStatus("success");
      }
    } catch (err) {
      console.error("Erro ao validar CPF:", err);
      setStatus("error");
      setError(err.response?.data?.error || "Erro ao validar CPF. Tente novamente.");
    }
  };

  return (
    <section className="contact-section">
      <h2>Validação de CPF</h2>
      <form onSubmit={handleSubmit} className="contact-form">
        <label htmlFor="cpf">Digite o CPF:</label>
        <input
          id="cpf"
          name="cpf"
          value={cpf}
          onChange={(e) => setCpf(e.target.value)}
          placeholder="Ex: 529.982.247-25"
        />
        <button type="submit" disabled={status === "loading"}>
          {status === "loading" ? "Validando..." : "Validar CPF"}
        </button>
      </form>

      {status === "success" && valid && <p className="success">✅ CPF válido!</p>}
      {status === "success" && valid === false && <p className="error">❌ CPF inválido!</p>}
      {status === "error" && <p className="error">{error}</p>}
    </section>
  );
}
